import { useEffect, useState } from "react";
import React from "react";
import { getTodos, deleteTodo } from "./api/todo";
import normilizeTodos from "./utils/normilize-todos";
import Todo from "./components/Todo";

function App() {
   //  const mockTodos = [
   //     { id: "1", title: "Needed text", completed: "true" },
   //     { id: "2", title: "Needed ", completed: "true" },
   //     { id: "3", title: "Text ", completed: "true" },
   //  ];
   const [todosIds, setTodosIds] = useState(null);
   const [todosByIds, setTodosByIds] = useState({});
   const [isLoadingTodos, setLoadingTodos] = useState(false);
   const [isError, setIsError] = useState(false);
   useEffect(() => {
      setIsError(false);
      setLoadingTodos(true);
      getTodos()
         .then((todos) => {
            const [ids, byIds] = normilizeTodos(todos);
            setLoadingTodos(false);
            setTodosIds(ids);
            setTodosByIds(byIds);
         })
         .catch((todos) => {
            setIsError(true);
            setLoadingTodos(false);
         });
   }, []);
   function handleDeleteTodo(id) {
      console.log(id);
      setTodosIds(todosIds.filter((todosIds) => todosIds !== id));
      deleteTodo(id);
   }
   function handleToggle(id) {
      setTodosByIds({
         ...todosByIds,
         [id]: {
            ...todosByIds[id],
            completed: !todosByIds[id],
         },
      });
   }
   return (
      <div>
         {isError && <p>Have are problem in server</p>}
         {isLoadingTodos && <p>Download all todos list</p>}
         <h1>Todos list</h1>
         {todosIds &&
            todosIds.map((id) => (
               <Todo
                  todo={todosByIds[id]}
                  onDeleteBtnClick={() => handleDeleteTodo(id)}
                  onToggle={() => handleToggle[id]}
               />
            ))}
      </div>
   );
}

export default App;
