import { useEffect, useState } from "react";
import React from "react";
import getTodos from "./api/todo";
import normilizeTodos from "./utils/normilize-todos";

function App() {
   //  const mockTodos = [
   //     { id: "1", title: "Needed text", completed: "true" },
   //     { id: "2", title: "Needed ", completed: "true" },
   //     { id: "3", title: "Text ", completed: "true" },
   //  ];
   const [todosIds, setTodosIds] = useState(null);
   const [todosByIds, setTodosByIds] = useState({});
   const [isLoadingTodos, setLoadingTodos] = useState(false);
   const [isError, setIsError] = useState(false);
   useEffect(() => {
      setIsError(false);
      setLoadingTodos(true);
      getTodos()
         .then((todos) => {
            const [ids, byIds] = normilizeTodos(todos);
            setLoadingTodos(false);
            setTodosIds(ids);
            setTodosByIds(byIds);
         })
         .catch((todos) => {
            setIsError(true);
            setLoadingTodos(false);
         });
   }, []);

   return (
      <div>
         {isError && <p>Have are problem in server</p>}
         {isLoadingTodos && <p>Download all todos list</p>}

         {todosIds &&
            todosIds.map((id) => (
               <div key={id}>
                  <p>
                     {todosByIds[id].title}{" "}
                     <input
                        type="checkbox"
                        defaultChecked={
                           todosByIds[id].completed ? "checked" : ""
                        }
                     />
                  </p>
               </div>
            ))}
      </div>
   );
}

export default App;
